import { makeStyles } from "@material-ui/styles";

// colors
import { colors } from "../../../App.style";

const localStyles = makeStyles(() => ({
    sidebar: {
        zIndex: '10',
        position: 'fixed',
        left: '0',
        top: '0',
        height: '100vh',
        width: '5rem',
        backgroundColor: colors.sidebarPrimary,
    },
    sidebarContent: {
        marginTop: '5rem',
        display: 'grid',
        gridAutoFlow: 'rows',
        gridTemplateRows: 'repeat(auto-fit, minmax(2rem, 1fr))',
        gap: '1rem',

        justifyItems: 'center',
    },
    sidebarContentList: {
        listStyle: 'none',
        display: 'grid',
        gridAutoFlow: 'rows',
        gridTemplateRows: 'repeat(auto-fit, minmax(2rem, 1fr))',
        gap: '1rem',
    }
}));

export default localStyles;
